import React, {useCallback, useState, useEffect} from 'react';
import axios from 'axios';
import '../styles/Common.css';
import '../styles/ImageUpload.css'

const ImageShare = (props) => {
    const {challenge, user} = props;
    const [imageDownload, setImageDownload] = useState({});
    const [image, setImage] = useState(null);
    var element = React.createRef();

    useEffect(() => {
        element.current.addEventListener('click', share)
    })

    useEffect(() => {
        var completedChallenge = user.challengesCompleted.find(compChal => compChal.challengeId === challenge.id)
        var data = {
            'key': completedChallenge.achievementImageKey
        }
        axios.post('/image/download', data)
            .then(response => setImageDownload(response.data))
            .catch(err => console.log(err))
    }, [challenge, user])

    useEffect(() => {
        if (imageDownload.url !== undefined && imageDownload.url !== null) {
            console.log("hi")
            fetch(imageDownload.url)
            .then(response => response.blob())
            .then(blob => {
                let file = new File([blob], 'download.jpg', { type: "image/jpg" });
                setImage(file)
            })
        }
    }, [imageDownload])

    const share = () => {
        if (navigator.canShare && navigator.canShare({ files: [image] })) {
            alert("yes")
            navigator.share({
              files: [image]
            //   title: 'Vacation Pictures',
            //   text: 'Photos from September 27 to October 14.',
            })
            .then(() => console.log('Share was successful.'))
            .catch((error) => alert(error));
          } else {
            console.log(`Your system doesn't support sharing files.`);
          }
    }

    return (
        <>
            <div className='row justify-content-md-center description-box'>
                <div className='col-sm-12 col-md-6'>
                    <div className='image-preview'>
                        {image ? 
                        <img src={window.URL.createObjectURL(image)} width='50%' className='image-preview'/>
                        : null}
                    </div>
                </div>
            </div>
            <div className='row justify-content-md-center description-box'>
                <div className='col-sm-12 col-md-6 text-center'>
                    {Object.keys(imageDownload).length === 0? 
                        <button className='button-rounded-black-disabled text-large' disabled>Share</button>:
                        <button className='button-rounded-black text-large' ref={element}>Share</button>
                    }
                </div>
            </div>
        </>
    )
}

export default ImageShare;
