import React, {useCallback, useState, useEffect} from 'react';
import axios from 'axios';
import '../styles/Common.css';
import '../styles/ImageUpload.css'

const ImageShare = (props) => {
    const {challenge, user} = props;
    const [imageDownload, setImageDownload] = useState({});

    useEffect(() => {
        var completedChallenge = user.challengesCompleted.find(compChal => compChal.challengeId === challenge.id)
        var data = {
            'key': completedChallenge.achievementImageKey
        }
        axios.post('/image/download', data)
            .then(response => setImageDownload(response.data))
            .catch(err => console.log(err))
    }, [challenge, user])


    return (
        <>
            <div className='row justify-content-md-center description-box'>
                <div className='col-sm-12 col-md-6'>
                    <div className='image-preview'>
                        <img src={imageDownload.url} width='50%' className='image-preview'/>
                    </div>
                </div>
            </div>
            <div className='row justify-content-md-center description-box'>
                <div className='col-sm-12 col-md-6 text-center'>
                    {Object.keys(imageDownload).length === 0? 
                        <button className='button-rounded-black-disabled text-large' disabled>Share</button>:
                        <button className='button-rounded-black text-large'>Share</button>
                    }
                </div>
            </div>
        </>
    )
}

export default ImageShare;
