import React, {useCallback, useState} from 'react';
import axios from 'axios';
import {useDropzone} from 'react-dropzone'
import '../styles/ImageUpload.css'

const ImageUpload = (props) => {
    const {challenge} = props;
    const [image, setImage] = useState();
    const [imageUpload, setImageUpload] = useState({});

    const onDrop = useCallback(acceptedFiles => {
        console.log(acceptedFiles[0])
        setImage(acceptedFiles[0])
        var data = {
            'fileName' : acceptedFiles[0].name,
            'fileType': acceptedFiles[0].type,
            'challengeId': challenge.id,
        }
        axios.post('/image/upload', data)
            .then(response => {
                setUser(response.data);
                setCookie('isah_id', response.data.id, {path: '/', maxAge: 315360000})
            })
            .catch(function (error){
                console.log(error);
            })
    }, []);

    const {getRootProps, getInputProps, isDragActive, ac} = useDropzone({
        onDrop,
        accept: 'image/*',
        multiple: false
    });

    return (
        <div className='row justify-content-md-center description-box'>
            <div className='col-sm-12 col-md-6'>
                <div {...getRootProps()} className='dropbox'>
                    <input {...getInputProps()} />
                    {
                        isDragActive ? 
                        <div className='drag-text'>Drop image here</div> :
                        <div className='drag-text'>Drag 'n' drop image here, or click to select a file</div>

                    }
                </div>
            </div>
        </div>
      )
}

export default ImageUpload;
