import React, {useEffect} from 'react';
import { useCookies } from 'react-cookie';
import axios from 'axios';
import Navbar from './Navbar';
import ChallengeView from './ChallengeView';
import '../styles/Common.css';

const Home = () => {
    const [cookies, setCookie] = useCookies(['auth']);

    useEffect(() => {
        axios.get('/users')
            .then(response => {
                this.setState({ todos: response.data });
            })
            .catch(function (error){
                console.log(error);
            })
    }, [])

    return(
        <React.Fragment>
            <Navbar />
            <div className='container'>
                <div className='my-container'>
                    <h1 className='h1-black'>Lets fill this world with positivity</h1>
                    <br></br>
                    <h4>Nominate your friends to:</h4>
                    <div className='row mt-2 mb-2'>
                        <div className='col-sm-12 col-md-6 col-lg-4'>
                            <ChallengeView 
                            bgcolor='#8cc251' 
                            title={'Talk/call your parents, have coffee with it'}
                            chapter={5}
                            minutes={20}
                            points={100}
                            />
                        </div>
                        <div className='col-sm-12 col-md-6 col-lg-4'>
                            <ChallengeView 
                            bgcolor='#ee4433'
                            title={'Call old friend'}
                            chapter={5}
                            minutes={20}
                            points={100}
                            />
                        </div>
                        <div className='col-sm-12 col-md-6 col-lg-4'>
                            <ChallengeView 
                            bgcolor='#2468ff'
                            title={'Yoga & Stretching'}
                            chapter={5}
                            minutes={20}
                            points={100}
                            />
                        </div>
                        <div className='col-sm-12 col-md-6 col-lg-4'>
                            <ChallengeView
                            bgcolor='#262626'
                            title={'Ask for old photos, discuss about them'}
                            chapter={5}
                            minutes={20}
                            points={100}
                            />
                        </div>
                        <div className='col-sm-12 col-md-6 col-lg-4'>
                            <ChallengeView
                            bgcolor='#041e3f'
                            title={'Make coffee, slowly, get relaxed'}
                            chapter={5}
                            minutes={20}
                            points={100}
                            />
                        </div>
                        <div className='col-sm-12 col-md-6 col-lg-4'>
                            <ChallengeView
                            bgcolor='#0099ff'
                            title={'Talk/call your parents, have coffee with it'}
                            chapter={5}
                            minutes={20}
                            points={100}
                            />
                        </div>
                    </div>
                </div>
            </div>
        </React.Fragment>
    )
}

export default Home;
