    const {challenge} = props;
    const [image, setImage] = useState();
    const [imageUpload, setImageUpload] = useState({});

    useEffect(() => {
        console.log(props)
    }, [props])

    const onDrop = useCallback(acceptedFiles => {
        console.log(acceptedFiles[0])
