import React, {useEffect, useState} from 'react';
import axios from 'axios';
import Navbar from './Navbar';
import StepsView from './StepsView';
import '../styles/Common.css';
import '../styles/Challenge.css';

const Challenge = (props) => {
    const [challenge, setChallenge] = useState({});
    const bgcolor = '#8cc251';

    useEffect(() => {
        console.log(props)
        axios.get('/challenges?id=' + props.match.params.id)
            .then(response => {
                setChallenge(response.data);
                console.log(response.data)
            })
            .catch(function (error){
                console.log(error);
            })
    }, [])

    return(
        <React.Fragment>
            <Navbar bgcolor={challenge.accentColor}/>
            <div className='container-fluid' style={{background : challenge.accentColor}}>
                <div className='top-bar'>
                    <div className='justify-content-start'>
                        <div className='row h-100 align-items-center'>
                            <div className='col d-none d-sm-block'>
                                <button className='button-rounded-transparent'>
                                    <span className='mr-3'><i class="fas fa-arrow-left"></i></span>
                                    Back
                                </button>
                            </div>
                            <div className='col d-xs-block d-sm-none'>
                                    <span className='mr-3'><i class="fas fa-arrow-left text-light"></i></span>
                            </div>
                        </div>
                    </div>
                    <div className='justify-content-end'>
                        <div className='row h-100 align-items-center'>
                            <div className='col d-none d-sm-block'>
                                <button className='button-rounded-transparent'>
                                    <span className='mr-3'><i class="fas fa-share"></i></span>
                                    Nominate
                                </button>
                            </div>
                            <div className='col d-xs-block d-sm-none'>
                                    <span className='mr-3'><i class="fas fa-share text-light"></i></span>
                            </div>
                        </div>
                    </div>
                </div>
                <div className='row justify-content-md-center description-box'>
                    <div className='col-sm-12 col-md-6'>
                    <h1 className='h1-white'>{challenge.name}</h1>
                    <p className='description-text'>{challenge.description}</p>
                    </div>
                </div>
            </div>
            <div className='container-fluid'>
                <div className='my-container'>
                    <StepsView/>
                </div>
            </div>
        </React.Fragment>
    )
}

export default Challenge;
