import React, {useCallback, useState, useEffect} from 'react';
import axios from 'axios';
import {useDropzone} from 'react-dropzone'
import '../styles/ImageUpload.css'

const ImageUpload = (props) => {
    const [image, setImage] = useState();
    const [imageUpload, setImageUpload] = useState({});

    const onDrop = useCallback(acceptedFiles => {
        console.log(acceptedFiles[0])
        setImage(acceptedFiles[0])
        getPresignedUrl(acceptedFiles[0]);
    }, []);

    const getPresignedUrl = (file) => {
        var data = {
            'challengeId': props.challenge.id,
            'fileName' : file.name,
            'fileType': file.type
        }
        axios.post('/image/upload', data)
            .then(response => {
                setImageUpload(response.data);
            })
            .catch(function (error){
                console.log(error);
            })
    }

    const {getRootProps, getInputProps, isDragActive, ac} = useDropzone({
        onDrop,
        accept: 'image/*',
        multiple: false
    });

    return (
        <div className='row justify-content-md-center description-box'>
            <div className='col-sm-12 col-md-6'>
                <div {...getRootProps()} className='dropbox'>
                    <input {...getInputProps()} />
                    {
                        isDragActive ? 
                        <div className='drag-text'>Drop image here</div> :
                        <div className='drag-text'>Drag 'n' drop image here, or click to select a file</div>

                    }
                </div>
            </div>
        </div>
      )
}

export default ImageUpload;
