import React, {useEffect, useState} from 'react';
import { useCookies } from 'react-cookie';
import axios from 'axios';
import Navbar from './Navbar';
import '../styles/Common.css';
import '../styles/Profile.css';

const Profile = () => {
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [user, setUser] = useState({});
    const [cookies, setCookie] = useCookies(['auth']);

    useEffect(() => {
        axios.post('/users')
            .then(response => {
                setUser(response.data);
                setName(response.data.name || '');
                setEmail(response.data.email || '');
                setCookie('auth', response.data._id, {path: '/', maxAge: 315360000});
            })
            .catch(function (error){
                console.log(error);
            })
    }, [])

    onSubmitHandler = () => {
        axios.put('/users')
            .then(response => {
                setUser(response.data);
                setName(response.data.name || '');
                setEmail(response.data.email || '');
                setCookie('auth', response.data._id, {path: '/', maxAge: 315360000});
            })
            .catch(function (error){
                console.log(error);
            })
    }

    return(
        <React.Fragment>
            <Navbar/>
            <div className='container'>
                <div className='my-container'>
                    <div className='row justify-content-md-center mt-4 mb-4'>
                        <div className='col-sm-12 col-md-6 account-row'>
                            <h1 className='h1-black'>Account Setting</h1>
                        </div>
                    </div>
                    <div className='row justify-content-md-center mt-4 mb-2'>
                        <div className='col-sm-12 col-md-6'>
                            <h5 className='label'>Name</h5>
                            <input
                            type='text'
                            className='input-field-large'
                            placeholder='Enter Your Name' 
                            value={name}
                            onChange={e => setName(e.target.value)}
                            />
                        </div>
                    </div>
                    <div className='row justify-content-md-center mt-2 mb-4'>
                        <div className='col-sm-12 col-md-6 account-row'>
                            <h5 className='label'>Email</h5>
                            <input
                            type='text'
                            className='input-field-large'
                            placeholder='Enter Your Email ID'
                            value={email}
                            onChange={e => setEmail(e.target.value)}
                            />
                        </div>
                    </div>
                    <div className='row justify-content-md-center mt-4 mb-4'>
                        <div className='col-sm-12 col-md-6 text-center'>
                            <button className='button-rounded-black text-large'>Update</button>
                        </div>
                    </div>
                </div>
            </div>
        </React.Fragment>
    )
}

export default Profile;
