import React, { useEffect } from 'react';
import '../styles/ChallengeView.css';
import '../styles/Common.css';

const ChallengeView = (props) => {
    const {bgcolor, title} = props
    return(
        <React.Fragment>
            <div className='challenge-box' style={{background : bgcolor}}>
                <p className='challenge-title'>{title}</p>
            </div>
            <div className='challenge-description' style={{borderColor: bgcolor}}>
                <div className='row h-100'>
                    <div className='col text-center'>
                        <p className='bottom-number'>5</p>
                    </div>
                    <div className='col text-center'>
                        <p className='bottom-number'>20</p>
                    </div>
                    <div className='col text-center'>5</div>
                </div>
            </div>
        </React.Fragment>
    )
}

export default ChallengeView;
